mapper.select:
SELECT COUNT(*)
  FROM board_master
 WHERE BOARD_ID = #{boardId}

mapper.delete:
DELETE FROM board_comment
 WHERE BOARD_IDX IN (
                         SELECT IDX
                           FROM board
                          WHERE MASTER_IDX = #{idx}
                    )

mapper.select:
SELECT COUNT(*)
  FROM content
 WHERE CONTENT_ID = #{contentId}

SELECT @ROWNUM:=@ROWNUM+1 AS RN, A.IDX, A.QA_SUBJECT, B.QA_CATEGORY, B.QA_STATUS, B.SECRET_YN, IF( A.CREATE_IDX IS NULL , NULL, IFNULL(C.USER_NICKNM,'탈퇴된 사용자')) AS CREATE_NICKNM, A.CREATE_DATE, D.USER_NICKNM AS UPDATE_NICKNM, A.UPDATE_DATE FROM qna A INNER JOIN qna_config B ON A.IDX = B.QNA_IDX LEFT OUTER JOIN users C ON A.CREATE_IDX = C.IDX LEFT OUTER JOIN users D ON B.UPDATE_IDX = D.IDX, (SELECT @ROWNUM:=0) temp WHERE PARENT_IDX IS NULL
AND B.QA_STATUS IN('Q' , 'RQ')
ORDER BY IDX LIMIT #{pageStart},#{amount}

SELECT COUNT(*) FROM ( SELECT A.IDX, A.QA_SUBJECT, B.QA_CATEGORY, B.QA_STATUS, B.SECRET_YN, IF( A.CREATE_IDX IS NULL , NULL, IFNULL(C.USER_NICKNM,'탈퇴된 사용자')) AS CREATE_NICKNM, A.CREATE_DATE, D.USER_NICKNM AS UPDATE_NICKNM, B.UPDATE_DATE FROM qna A INNER JOIN qna_config B ON A.IDX = B.QNA_IDX LEFT OUTER JOIN users C ON A.CREATE_IDX = C.IDX LEFT OUTER JOIN users D ON B.UPDATE_IDX = D.IDX WHERE PARENT_IDX IS NULL
AND B.QA_STATUS IN('Q' , 'RQ')
) AS CNT_TB

mapper.update:
UPDATE qna_config
   SET QA_STATUS = #{qaStatus},
       UPDATE_DATE = SYSDATE(),
       UPDATE_IDX = #{userIdx}
 WHERE QNA_IDX = #{qnaIdx}

mapper.select:
SELECT USER_EMAIL
  FROM users
 WHERE IDX = #{idx}

SELECT A.IDX, D.FAQ_NM, A.FAQ_QUESTION, A.FAQ_ANSWER, A.FAQ_ORDER, A.USE_YN, IFNULL(B.USER_NICKNM,'탈퇴된 관리자')AS CREATE_NICKNM, A.CREATE_DATE, IFNULL(C.USER_NICKNM,'탈퇴된 관리자')AS UPDATE_NICKNM, A.UPDATE_DATE FROM faq A LEFT JOIN users B ON A.CREATE_IDX = B.IDX LEFT JOIN users C ON A.UPDATE_IDX = C.IDX LEFT JOIN faq_master D ON A.MASTER_IDX = D.IDX WHERE 1 = 1
AND MASTER_IDX = #{paramMap.masterIdx}
ORDER BY FAQ_ORDER IS NULL, FAQ_ORDER, CREATE_DATE
