mapper.delete:
DELETE FROM USERS
 WHERE USER_IDX = #{idx}
   AND ROLE_TYPE IN

    #{roleType}

mapper.update:
UPDATE USERS
   SET USER_PWD = #{userPwd}
 WHERE IDX = {idx}

mapper.select:
SELECT EXISTS(
                SELECT *
                  FROM USERS
                 WHERE IDX = #{idx}
                   AND USER_PWD = #{userPwd}
             )
