mapper.select:
SELECT COUNT(*)
  FROM USERS
 WHERE user_id = #{userId}

SELECT EXISTS(
               SELECT VERIFICATION_CODE
               FROM USERS
               WHERE USER_ID = #{userId}
                 AND VERIFICATION_CODE = #{code}
             ) AS EXIST

SELECT EXISTS(
                SELECT USER_NICKNM
                  FROM USERS
                 WHERE USER_NICKNM = #{userNicknm}
             )

SELECT EXISTS(
               SELECT USER_EMAIL
               FROM USERS
               WHERE USER_EMAIL = #{userEmail}
           )
